// Helper functions to get different types of blog posts
export const getPostsByType = (type: "full" | "mini") => {
  return blogPosts.filter(post => post.type === type);
};

export const getFeaturedPosts = () => {
  return blogPosts.filter(post => post.type === "full").slice(0, 3);
};

export const getRecentPosts = () => {
  return blogPosts.filter(post => post.type === "full").slice(0, 4);
};
